import"./hoisted.Bza_LTLr.js";document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".toggle-button").forEach(e=>{e.addEventListener("click",function(){const s=e.getAttribute("data-index"),n=document.getElementById(`card-content-${s}`),t=e.querySelector(".icon i");n.classList.contains("active")?(n.classList.remove("active"),t.classList.remove("fa-angle-up"),t.classList.add("fa-angle-down")):(n.classList.add("active"),t.classList.remove("fa-angle-down"),t.classList.add("fa-angle-up"))})})});
